Scripts.OnClick:
KA_RaidTrackerAcceptDeleteFrame.id = this:GetParent().raidid;
KA_RaidTrackerAcceptDeleteFrame.typeid = this:GetParent().playername;
KA_RaidTrackerAcceptDeleteFrame.type = "player";
KA_RaidTrackerAcceptDeleteFrame.header = "Delete Player";
KA_RaidTrackerAcceptDeleteFrame:Show();

Scripts.OnEnter:
getglobal(this:GetParent():GetName() .. "DeleteText"):SetTextColor(0.8, 0.5, 0);
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to delete", 1, 0.7, 0);
GameTooltip:Show();
getglobal(this:GetParent():GetName() .. "MouseOver"):Show();

GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to edit note", 1, 0.7, 0);
if(KARaidTrackerDB.RaidLog[this:GetParent().raidid]["PlayerInfos"][this:GetParent().playername] and KARaidTrackerDB.RaidLog[this:GetParent().raidid]["PlayerInfos"][this:GetParent().playername]["note"]) then
	GameTooltip:AddLine(KARaidTrackerDB.RaidLog[this:GetParent().raidid]["PlayerInfos"][this:GetParent().playername]["note"], 0.6, 0.6, 0.6);
end
GameTooltip:Show();
getglobal(this:GetParent():GetName() .. "MouseOver"):Show();

Scripts.OnClick:
KA_RaidTrackerAcceptDeleteFrame.id = this:GetParent().raidid;
KA_RaidTrackerAcceptDeleteFrame.typeid = this:GetParent().playername
KA_RaidTrackerAcceptDeleteFrame.type = "player";
KA_RaidTrackerAcceptDeleteFrame.header = "Delete Player";
KA_RaidTrackerAcceptDeleteFrame:Show();

Scripts.OnEnter:
getglobal(this:GetParent():GetName() .. "DeleteText"):SetTextColor(0.8, 0.5, 0);
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to delete", 1, 0.7, 0);
GameTooltip:Show();
getglobal(this:GetParent():GetName() .. "MouseOver"):Show();

Scripts.OnClick:
KA_RaidTrackerEditNoteFrame.type = "the raid entry";
KA_RaidTrackerEditNoteFrame.text = this:GetParent().raidtitle;
KA_RaidTrackerEditNoteFrame.color = "ff00ff00";
KA_RaidTrackerEditNoteFrame.id = this:GetParent().raidid;
KA_RaidTrackerEditNoteFrame.playerid = this:GetParent().playername;
KA_RaidTrackerEditNoteFrame:Show();

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to edit note", 1, 0.7, 0);
if(KARaidTrackerDB.RaidLog[this:GetParent().raidid]["PlayerInfos"][this:GetParent().playername] and KARaidTrackerDB.RaidLog[this:GetParent().raidid]["PlayerInfos"][this:GetParent().playername]["note"]) then
	GameTooltip:AddLine(KARaidTrackerDB.RaidLog[this:GetParent().raidid]["PlayerInfos"][this:GetParent().playername]["note"], 0.6, 0.6, 0.6);
end
GameTooltip:Show();
getglobal(this:GetParent():GetName() .. "MouseOver"):Show();

Scripts.OnLoad:
getglobal(this:GetName() .. "MouseOver"):SetAlpha(0.25);
getglobal(this:GetName() .. "MouseOver"):SetVertexColor(1, 1, 0);
getglobal(this:GetName() .. "MouseOverLeft"):SetAlpha(0.45);
getglobal(this:GetName() .. "MouseOverLeft"):SetVertexColor(1, 1, 0);
getglobal(this:GetName() .. "MouseOverRight"):SetAlpha(0.45);
getglobal(this:GetName() .. "MouseOverRight"):SetVertexColor(1, 1, 0);

Scripts.OnClick:
KA_RaidTrackerAcceptDeleteFrame.id = KA_RaidTrackerFrame.selected;
KA_RaidTrackerAcceptDeleteFrame.type = "raid";
KA_RaidTrackerAcceptDeleteFrame.header = "Delete Raid";
KA_RaidTrackerAcceptDeleteFrame:Show();

                    	if ( not KA_RaidTrackerFrame.type ) then
                        KA_RaidTrackerFrame.type = "events";
                    	elseif ( KA_RaidTrackerFrame.type == "events" ) then
                        KA_RaidTrackerFrame.type = "raids";
                      else
                      	KA_RaidTrackerFrame.type = "events";
                      end
											KA_RaidTracker_UpdateView();

                        KA_RaidTracker_GetPage();
                        if ( KA_RaidTrackerFrame.type == "raids" or KA_RaidTrackerFrame.type == "events" or not KA_RaidTrackerFrame.type ) then
	                        KA_RaidTrackerFrame.type = "items";
	                        this:SetText("View Players");
                        elseif ( KA_RaidTrackerFrame.type == "items" ) then
	                        KA_RaidTrackerFrame.type = "raids";
	                        this:SetText("View Items");
                        elseif ( KA_RaidTrackerFrame.type == "player" ) then
	                        KA_RaidTrackerFrame.type = "playeritems";
	                        this:SetText("View Raids");
                        elseif ( KA_RaidTrackerFrame.type == "playeritems" ) then
	                        KA_RaidTrackerFrame.type = "player";
	                        this:SetText("View Loot");
	                      end
                        KA_RaidTracker_UpdateView();

Scripts.OnShow:
if ( not this.selected ) then
this.type = "raids";
if ( getn(KARaidTrackerDB.RaidLog) > 0 ) then
this.selected = 1;
end
end
KA_RaidTracker_Update();
KA_RaidTracker_UpdateView();

if(this.type == "item") then
	this.itemplayer = KARaidTrackerDB.RaidLog[this.id]["Loot"][this.typeid]["player"]
	this.itemitemid = KARaidTrackerDB.RaidLog[this.id]["Loot"][this.typeid]["item"]["id"]
	this.itemtime = KARaidTrackerDB.RaidLog[this.id]["Loot"][this.typeid]["time"]
end
getglobal(this:GetName() .. "HeaderText"):SetText("Are you sure you want to delete this " .. this.type .. "?");
getglobal(this:GetName() .. "HeaderText"):SetText(this.header);
getglobal(this:GetName() .. "Info"):SetText("Are you sure you want to delete this " .. this.type .. "?");
PlaySound("UChatScrollButton");

		this.itemitemid = KARaidTrackerDB.RaidLog[this.raidid]["Loot"][this.itemid]["item"]["id"];
		this.itemtime = KARaidTrackerDB.RaidLog[this.raidid]["Loot"][this.itemid]["time"];
		this.itemplayer = KARaidTrackerDB.RaidLog[this.raidid]["Loot"][this.itemid]["player"];

    local itemname = KARaidTrackerDB.RaidLog[this.raidid]["Loot"][this.itemid]["item"]["name"];
    local itemcolor = KARaidTrackerDB.RaidLog[this.raidid]["Loot"][this.itemid]["item"]["c"];
    getglobal(this:GetName() .. "Editing"):SetText("Editing cost for \"|c" .. itemcolor .. "" .. itemname .. "|r\"");
		text = KARaidTrackerDB.RaidLog[this.raidid]["Loot"][this.itemid]["costs"];

		if (text) then
			getglobal(this:GetName() .. "NoteEB"):SetText(text);
			getglobal(this:GetName() .. "NoteEB"):HighlightText();
		end;
